const PresensiPage = () => {
    const [data, setData] = useState('No result');
    const [dataMHS, setdataMHS] = useState({
        formData: "",
        nim: []
    })
    function handleClear(e) {
        e.preventDefault();
        setData('No result');
    }
    const handleSubmit = (event) => {
        console.log("submitted")
