    }
    return(
        <div className={classes.content}>
            <Header/>
            <div>
                <QrReader
                    onResult={(result, error) => {
                    if (!!result) {
                        setData(result?.text);
                    }

                    if (!!error) {
                        console.info(error);
                    }
                    }}
                    style={{ width: '100%'}}
                />
            </div>
            <p>Qrcode Data :</p>
            <p>{data}</p>
            <button onClick={handleClear}>Clear QR Data</button>
            <div>
                <p>Input your NIM : </p>    
