                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        {/* <th>No</th> */}
                        <th>No</th>
                        <th>NIM</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {dataMHS.nim.map(( data, index) => 
                        <tr key={index}>
                            <td>{index+1}</td>
                            <td>{data}</td>
                            <td><p>delete</p></td>
                        </tr>
                    )}
                </tbody>

            </table>
            <button onClick={presensi}>Presensi!</button>
